DROP TABLE IF EXISTS `rer_job`;
CREATE TABLE `rer_job` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻表',
  `pid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '一级分类id',
  `ty` mediumint(8) NOT NULL DEFAULT '0' COMMENT '二级分类id',
  `tty` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '三级分类',
  `user_id` int(10) NOT NULL DEFAULT '0' COMMENT '企业会员id',
  `cityid` varchar(200) NOT NULL COMMENT '多城市id',
  `address` varchar(255) NOT NULL COMMENT '发布地址',
  `industryid` int(11) NOT NULL COMMENT '行业类型id ',
  `positionid` int(11) NOT NULL COMMENT '职位类别',
  `destination` varchar(200) NOT NULL COMMENT '路线目的地',
  `starttime` varchar(30) NOT NULL COMMENT '游学开始时间',
  `endtime` varchar(30) NOT NULL COMMENT '结束时间',
  `bstarttime` varchar(30) NOT NULL COMMENT '报名开始时间',
  `bendtime` varchar(30) NOT NULL COMMENT '报名结束时间',
  `nature` int(11) NOT NULL COMMENT '企业性质',
  `work_nature` int(11) NOT NULL COMMENT '工作性质',
  `salary` int(1) NOT NULL COMMENT '月薪',
  `education` int(11) NOT NULL COMMENT '学历',
  `experience` int(11) NOT NULL COMMENT '经验要求',
  `name` varchar(200) NOT NULL,
  `enroll_num` int(11) NOT NULL DEFAULT '0' COMMENT '投递简历个数',
  `recruit_num` varchar(200) NOT NULL COMMENT '招收人数',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `price` decimal(10,2) NOT NULL,
  `link1` varchar(100) NOT NULL,
  `link2` varchar(100) NOT NULL,
  `ftitle` varchar(150) NOT NULL DEFAULT '',
  `linkurl` varchar(250) NOT NULL DEFAULT '' COMMENT '跳链接',
  `introduce` text COMMENT '摘要',
  `content` longtext COMMENT '内容',
  `content2` text NOT NULL,
  `content3` text NOT NULL,
  `content4` text NOT NULL,
  `img1` varchar(255) NOT NULL COMMENT '标题图片',
  `img2` varchar(255) NOT NULL DEFAULT '',
  `img3` varchar(255) NOT NULL DEFAULT '',
  `img4` varchar(255) NOT NULL DEFAULT '',
  `img5` varchar(255) NOT NULL DEFAULT '',
  `img6` varchar(255) NOT NULL DEFAULT '',
  `file` varchar(30) NOT NULL DEFAULT '' COMMENT '附件',
  `source` varchar(150) NOT NULL DEFAULT '' COMMENT '来源',
  `author` varchar(50) NOT NULL DEFAULT '' COMMENT '作者',
  `seotitle` varchar(100) NOT NULL DEFAULT '' COMMENT 'seo标题',
  `keywords` varchar(100) NOT NULL DEFAULT '' COMMENT '关键字',
  `description` longtext,
  `hits` mediumint(9) NOT NULL DEFAULT '0' COMMENT '点击数',
  `begin` varchar(110) NOT NULL,
  `isgood` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `istop` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶',
  `issued` tinyint(11) NOT NULL DEFAULT '1' COMMENT '是否发布',
  `isstate` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否显示',
  `disorder` mediumint(9) NOT NULL DEFAULT '0' COMMENT '排序',
  `sendtime` varchar(30) NOT NULL COMMENT '添加时间',
  `relative` varchar(200) NOT NULL COMMENT '亮点标签',
  `content5` text NOT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `deleted_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `news` (`pid`,`ty`,`title`,`isgood`,`isstate`,`disorder`,`sendtime`)
) ENGINE=MyISAM AUTO_INCREMENT=274 DEFAULT CHARSET=utf8 COMMENT='职业招聘表';

